USE test;
DROP TABLE IF EXISTS book;

CREATE TABLE book(
    id INT(11) NOT NULL AUTO_INCREMENT,
    title VARCHAR(100) NOT NULL,
    author VARCHAR(100) NOT NULL,
    print_year INT(11),
    isbn VARCHAR(20),
    read_already bit(1) NOT NULL DEFAULT 0,
    description VARCHAR(255),
    PRIMARY KEY (id))
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8;


